import React, { useState } from "react";
import "./HumanBody.css";

const HumanBody = () => {
  const [selectedArea, setSelectedArea] = useState("");

  const handlePieceClick = (event) => {
    const position =
      event.target.getAttribute("data-position") ||
      event.target.parentElement.getAttribute("data-position");
    if (position) {
      setSelectedArea(position);
    }
  };

  return (
    <div>
      <div className="human-body" onClick={handlePieceClick}>
        {/* Replace with your SVG elements, ensure to add data-position */}
      </div>
      {selectedArea && (
        <div id="area">
          <div id="data">{selectedArea}</div>
        </div>
      )}
    </div>
  );
};

export default HumanBody;
