import React from "react";
import DHIlab from "./DHIlab/DHIlab";

const Navbar = () => {
  // Inline styles for the navbar
  const navbarStyles = {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "20px",
    backgroundColor: "#f8f9fa", // Light background color for the navbar
    borderBottom: "2px solid #ddd", // Adding a subtle border
  };

  const logoStyles = {
    display: "flex",
    alignItems: "center",
  };

  const titleStyles = {
    marginLeft: "10px",
    fontSize: "24px",
    fontWeight: "bold",
    color: "#333", // Darker text color for visibility
  };

  const experimentVersionStyles = {
    fontSize: "18px",
    fontWeight: "300",
    color: "#888", // Light gray color for experiment version text
    marginRight: "20px",
  };

  const formStyles = {
    display: "flex",
    alignItems: "center",
  };

  const labelStyles = {
    fontSize: "16px",
    marginRight: "10px",
  };

  const rangeStyles = {
    width: "150px", // Adjusted width for better control
    cursor: "pointer", // Indicates to the user that it's interactive
  };

  return (
    <div id="Navbar">
      <header style={navbarStyles}>
        <div style={logoStyles}>
          <DHIlab />
          <h3 style={titleStyles}>Symptom Checker</h3>
        </div>
        <h4 style={experimentVersionStyles}>(Experiment Version)</h4>
        <div>
          <form style={formStyles}>
            <label htmlFor="fontSizeRange" style={labelStyles}>
              Text Size:
            </label>
            <input
              type="range"
              id="fontSizeRange"
              min="10"
              max="30"
              defaultValue="16"
              onInput={(e) => updateTextSize(e.target.value)}
              style={rangeStyles}
            />
          </form>
        </div>
      </header>
    </div>
  );
};

// JavaScript function to update the base font size
const updateTextSize = (size) => {
  document.documentElement.style.setProperty("--base-font-size", size + "px");
};

export default Navbar;
