sName:"diseaseSymptoms"},/*#__PURE__*/React.createElement("h4",null,"Disease Symptoms"),/*#__PURE__*/React.createElement("ul",null,key.disease_symptom.sort().map(function(item,index){return key.symptom_user_has.includes(item)?/*#__PURE__*/React.createElement("li",{key:index,className:"active"},item):/*#__PURE__*/React.createElement("li",{key:index},item);}))));})),/*#__PURE__*/React.createElement("div",null,"Always visit a doctor if you have any symptoms of a disease or call your local hospital")):/*#__PURE__*/React.createElement(React.Fragment,null,/*#__PURE__*/React.createElement("div",{className:"grid-row width-full DiseaseComponent"},/*#__PURE__*/React.createElement("div",{className:"col-12 tablet:grid-col-12 patientInfo"},/*#__PURE__*/React.createElement("h3",null,"Patient gender: Male"),/*#__PURE__*/React.createElement("h3",null,"Patient age: 71")),/*#__PURE__*/React.createElement("p",null,"Cannot determine possible diseases due to lack of symptoms. Please retry the analysis with actual symptoms or call your local hospital if it is an emergency.")));};return _this;}_createClass(Disease,[{key:"render",value:function render(){return/*#__PURE__*/React.createElement(React.Fragment,null,this.get_current_html());}}]);return Disease;}(Component);export default Disease;

